public class Week3Part1StartingLoops {
	/** 
	 * @param args
	 */ 
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Loops - Counter controlled repetitions
		
		// A loop that counts from 1 to 10 
		int count;  // control variable
		count = 1; // initial value
		
		while (count < 11) {  // loop continuation condition - will either be true or false
			System.out.println("Count is: " + count);
			count++;  //Increment
		} // end of while loop 
	} // end of main method 
} // end of class   
